INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (1, 'Brompton Immortals', 500, 50, 'Парфюм Brompton Immortals создан эксклюзивно для знаменитого лондонского универмага Harrods. Он назван в честь расположенной поблизости фешенебельной улицы Бромптон, а его восточная направленность навеяна древнеегипетской легендой о цветочных ароматах, приносящих бессмертие.', 2);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (1, 'Honoré Delights', 500, 100, 'Honoré Delights – восьмой аромат из новой женской коллекции бренда LES INTERDITES (Запрещенные), который продолжает призывать нас поддаться запрещенным удовольствиям. Композиция посвящена восхитительному воздушно-кремовому десерту St-Honoré', 2);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (1, 'In Paradise Riviera', 1990, 50, 'Paradise Riviera – лимитированный весенний фланкер знаменитого аромата марки Lust In Paradise. Раскрывается в начале звонки и нотами бергамота. Они сменяются сладостными цветочными аккордами тиаре, пиона и туберозы, которые продолжают звучать и в шлейфе. База на основе мускуса и кашмерана окутывает мягким легким облаком.', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (1, 'Gold Immortals', 1750, 100, 'Парфюм Gold Immortals — новая версия эксклюзивного аромата Brompton Immortals, созданного для знаменитого лондонского универмага Harrods. Также как и оригинал, новинка посвящена древнеегипетской легенде о цветах, приносящих бессмертие.', 9);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (1, 'Outcast Blue', 1290, 100, 'Парфюм Outcast Blue создан эксклюзивно для знаменитого универмага Selfridges. Он посвящен новому дерзкому поколению артистов и художников, принесшему нам такое необычное явление, как провокационная поп-культура.', 9);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (1, 'Fleur Narcotique', 1180, 150, 'Композиция «Наркотического цветка» открывается сочными нотами бергамота, экзотического личи и бархатистого персика, переплетаясь в сердце с цветочным коктейлем из жасмина, душистого пиона и нежного флердоранжа. Завершается пирамидка древесными оттенками, которые дополнены нотами горького дубового мха и белого мускуса.', 9);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (2, 'Venenum Kiss', 1470, 100, 'Venenum Kiss Ex Nihilo (Ядовитый Поцелуй Экс Нихило) — унисекс (ближе к женскому) восточный цветочный аромат от Ex Nihilo, созданный парфюмером Quentin Bisch. ', 9);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (2, 'Bois d’Hiver', 990, 50, 'Bois d’Hiver – это аромат на грани силы и мягкости для каждой женщины, мечтающей чувствовать себя «а-ля парижанка». Название парфюма переводится как «Зимний Лес» и является ароматической интерпретацией железного кулака в бархатной перчатке.', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (2, 'Amber Sky', 1000, 50, 'Летняя новинка 2016 от Ex Nihilo создана парфюмером Оливье Пешо, который знаменит своими глубокими чувственными восточными композициями. Парфюм Amber Sky призван передать нам красоту волшебного заката у берегов Босфора.', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (2, '10019 Wonders', 840, 50, '10019 Wonders — невероятно вкусный восточный парфюм с ярким современным звучанием и стойким эффектным шлейфом. На старте вас ждет цветочная сладость апельсиновых цветов в окружении миндального молочка.', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (2, 'Iris Porcelana', 390, 100, 'ris Porcelana – полупрозрачный летящий аромат. В начальных нотах пирамида раскрывается освежающими тонами листа фиалки. Главную роль в композиции играет влажный акватический ирис. ', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (2, 'Sloane Ravers', 390, 100, 'Парфюм Sloane Ravers создан эксклюзивно для магазина Harvey Nichols, и передает его авангардный дух. Аромат посвящен рейверам, представителям скандальной поп-культуры начала 2000-х, очень популярной в США.', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (3, 'Viper Green', 399, 100, 'Ex Nihilo Viper Green — четвертый аромат из новой женской коллекции бренда LES INTERDITES (Запрещенные), который продолжает призывать нас поддаться запрещенным удовольствиям.', 2);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (3, 'Sweet Morphine', 610, 100, 'Ex Nihilo Sweet Morphine – первый аромат, давший старт новой женской коллекции бренда. Название этих духов в розовом флаконе переводится как «Сладкий Морфин».', 2);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (3, 'Love Shot', 290, 50, 'Ex Nihilo Love Shot — второй аромат из новой женской коллекции бренда LES INTERDITES (Запрещенные), который вслед за дебютным ароматом «Сладкий Морфин» призывает нас поддаться запрещенным удовольствиям.', 5);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (3, 'Vendomania', 330, 50, 'Ex Nihilo Vendomania – седьмой аромат из новой женской коллекции бренда LES INTERDITES (Запрещенные), который продолжает призывать нас поддаться запрещенным удовольствиям. ', 5);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (3, 'Devil Tender', 1160, 100, 'Ex Nihilo Devil Tender – третий аромат из новой женской коллекции бренда LES INTERDITES (Запрещенные), который вслед за Sweet Morphine и Love Shot призывает нас поддаться запрещенным удовольствиям.', 4);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (3, 'Vetiver Moloko', 1470, 50, 'Ex Nihilo Vetiver Moloko (Ветивер Молоко) – это, с одной стороны, землистый и мужской, с другой – женский и молочный парфюм. Эта двойственность аромата элегантно проявляется, когда его симфония развивается на коже.', 4);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (4, 'Citizen X', 1410, 100, 'Майская новинка 2017 года парфюм Citizen X воплощает образ позитивного и успешного современного жителя мегаполиса.', 9);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (4, 'Collection Signature', 1410, 50, 'Эксклюзивная премьера парфюма Collection Signature состоялась осенью 2018 года в рамках Vogue fashion’s Night Out в ЦУМе. На мировой рынок аромат поступит только через год, а до этого лимитированную партию будут продавать исключительно в России.', 9);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (4, 'Cologne 352', 3120, 50, 'Парфюм Cologne 352 EX Nihilo собрал в себе три разновидности одеколонов. Верхние ноты — сочные бодрые цитрусы со свежей зеленью – это несомненно итальянский классический одеколон.', 8);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (4, 'Honoré Delights', 4120, 50, 'Honoré Delights – восьмой аромат из новой женской коллекции бренда LES INTERDITES (Запрещенные), который продолжает призывать нас поддаться запрещенным удовольствиям.', 8);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (4, 'Nocturama', 1870, 2800, 'Парфюм посвящен музыкальному альбому Nocturama английского рок-певца Ника Кейва, большинство песен которого отличаются мрачной романтикой.', 1);
INSERT INTO products (types_id, name, price, weight, description, countries_id) VALUES (4, 'French Affair', 2030, 200, 'Парфюм French Affair — осенняя новинка 2017 года от бренда Экс Нихило. В нем  воссоздана атмосфера Парижа 70-х годов, с его незабываемым сочетанием роскоши, очарования и бунтарского духа.', 1);
